export default function Card({ card, onCardClick, onDelCard, onCardLike }) {
  const currentUser = useContext(CurrentUserContext);

  // Проверяем наличие карточки и ее владельца
  const isOwn = card && card.owner === currentUser._id;

  // Проверяем наличие карточки и ее лайков
  const isLiked = card && card.likes && card.likes.some((i) => i === currentUser._id);

  // В случае, если card.likes не определен, предполагаем, что это пустой массив
  const likesCount = card && card.likes ? card.likes.length : 0;

  const cardLikeBtnClassName = `place__like-btn style-btn ${
    isLiked && "place__like-btn_active"
  }`;

  const handleLikeClick = () => {
    onCardLike(card);
  };

  return (
    <li className="place">
      {card && (
        <>
          <img
            src={card.link}
            alt={card.name}
            className="place__image"
            onClick={() => onCardClick({ link: card.link, name: card.name })}
          />
          <div className="place__info">
            <h2 className="place__title"> {card.name} </h2>
            <div className="place__like-container">
              <button
                className={cardLikeBtnClassName}
                type="button"
                aria-label="Лайк"
                onClick={handleLikeClick}
              />
              <span className="place__likes-number">{likesCount}</span>
            </div>
          </div>
          {isOwn && (
            <button
              className="place__delete-btn style-btn"
              type="button"
              aria-label="Корзина"
              onClick={() => onDelCard(card._id)}
            />
          )}
        </>
      )}
    </li>
  );
}
